mapper.insert:
INSERT INTO HOSPITAL(
SEQ
, BUSINESSNUMBER
, CATEGORY1
, CATEGORY2
, NAME
, OPTIONNAME
, PRICE
, LOCATION
, T_IMAGE
, IMAGE
) VALUES(
20||HOSPI_SEQ.NEXTVAL
, #{businessNumber}
, #{category1, jdbcType=VARCHAR}
, #{category2}
, #{name}
${opname}
${opprice}
, #{location}
, #{t_image}
, #{image}
)

mapper.select:
SELECT B.* FROM(SELECT ROWNUM RN, A.* FROM(
SELECT SEQ ,CATEGORY1 ,CATEGORY2 ,IMAGE ,T_IMAGE ,NAME ,OPTIONNAME ,PRICE ,BUSINESSNUMBER ,LOCATION FROM HOSPITAL WHERE CATEGORY1 = #{category1}
AND CATEGORY2 IN
#{cate}
ORDER BY SEQ
) A) B WHERE RN BETWEEN #{start} AND #{end}

SELECT B.* FROM(SELECT ROWNUM RN, A.* FROM(
SELECT SEQ ,CATEGORY1 ,CATEGORY2 ,IMAGE ,T_IMAGE ,NAME ,OPTIONNAME ,PRICE ,BUSINESSNUMBER ,LOCATION FROM HOSPITAL WHERE CATEGORY1 = #{category1}
AND CATEGORY2 IN
#{cate}
AND LOCATION = #{location} ORDER BY SEQ
) A) B WHERE RN BETWEEN #{start} AND #{end}
