PNDNUMBER
,CATEGORY
,PAYDATE
,SUMPRICE
,MEMBERID
,BUYSTATE
,BUSINESSNUMBER
)VALUES(
(SELECT NVL(MAX(PNDNUMBER),0)+1 FROM PAYANDDELIVERY)
,#{category}
,SYSDATE
,#{sumPrice}
,#{memberId}
,'결제완료'
,#{businessNumber}
)
